mapper.select:
SELECT ccheck.*,doctor.name AS doctorName,basicdata.name AS patientName, checkDepartment.checkDepartmentName,checkitem.checkItemName FROM ccheck INNER JOIN doctor ON ccheck.doctorID=doctor.doctorID INNER JOIN patient ON ccheck.patientID=patient.patientID INNER JOIN basicdata ON basicdata.basicID=patient.basicID INNER JOIN checkDepartment ON ccheck.checkDepartmentID=checkDepartment.checkDepartmentID INNER JOIN checkitem ON ccheck.checkItemID=checkitem.checkItemID WHERE ccheck.effectiveNot=1 AND ccheck.reportTime IS null
and basicdata.name like concat('%', #{name,jdbcType=VARCHAR}, '%')
and ccheck.applyTime = #{applyTime,jdbcType=VARCHAR}
